import React, { Component } from 'react';
import { Map, Marker } from "pigeon-maps"

class Geolocalizacao extends React.Component {

    constructor(props) {
        super(props);
        this.state = { posicao: [] };
    }

    /*buscarPosicao = () => {
        let self = this;
        navigator.geolocation.getCurrentPosition(function (position) {
            console.log(position);
            self.setState({ latitude: position.coords.latitude, longitude: position.coords.longitude });
        });
    }*/

    /*buscarPosicao = () => {
        navigator.geolocation.getCurrentPosition(function (position) {
            const posicao = [position.coords.latitude, position.coords.longitude];
            console.log(posicao);
            return posicao;
        });
    };*/


    componentDidMount() {
        let self = this;
        navigator.geolocation.getCurrentPosition(function (localizacao) {
            localizacao = new navigator.geolocation.getCurrentPosition({ enableHighAccuracy:true });
            self.setState({ latitude: localizacao.coords.latitude, longitude: localizacao.coords.longitude });
        });
    }

    //posicao = [-29.671424, -51.1148032];

    //localizacao = new navigator.geolocation.getCurrentPosition({ enableHighAccuracy:true });

    /*localizacaoPrecisa(latitude, longitude) {
        latitude = this.localizacao.coords.latitude;
        longitude = this.localizacao.coords.longitude;
        console.log(latitude);
    }*/

    render() {
        return (
            <div>
                <h3 style={{ marginTop: '30px', marginBottom: '1px' }}>Obtenha sua localização</h3>
                {/* <span>Permita acesso no seu navegador.</span> */}
                <br />
                <Map height={280} width={460} defaultCenter={[this.state.posicao.latitude, this.state.posicao.longitude]} defaultZoom={17}>
                    <Marker width={50} color={'red'} anchor={[this.state.posicao.latitude, this.state.posicao.longitude]} />
                </Map>
            </div>
        );
    }
}

export default Geolocalizacao;
