import React, { Component } from 'react';
import iconeClock from './icone-clock.png';
import $ from "jquery";


class RegistroPonto extends React.Component {

    constructor(props) {
        super(props);
        this.state = { date: new Date() };
        this.getLocation.bind(this);
        this.showPosition.bind(this);
    }

    getLocation = () => {
        var x = document.getElementById("demo");

        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(showPosition);
        } else { 
          x.innerHTML = "Geolocation is not supported by this browser.";
        }
    }

    showPosition = (position) => {
        var x = document.getElementById("demo");

        x.innerHTML = "Latitude: " + position.coords.latitude + 
        "<br>Longitude: " + position.coords.longitude;
    }

    registrar() {
        $.ajax({
            url: 'http://localhost:8080/registroPonto/salvar',
            type: 'POST',
            data: {},
            success: function (result) {
            
            }
        });
    }

    render() {
        console.log(this.showPosition())
        return (
            <div style={{ marginTop: "10px" }}>
                <p id="demo"></p>

                <label style={{ backgroundColor: "orange", color: "white", padding: "4px", borderRadius: "8px" }}>Aguardando Ponto</label>
                <br />
                <br />
                
                <br />
                <br />
                <button onClick={this.registrar} style={{ borderRadius: "10px", backgroundColor: "lightGreen", padding: "10px" }}>
                    <img style={{ width: '13px' }} src={iconeClock} /> <label style={{ fontSize: '15px'}}>Registrar</label>
                </button>
            </div>
        );
    }
}

export default RegistroPonto;
