import React, { Component } from 'react';
import iconeClock from './icone-clock.png';
import $ from "jquery";
import { GoogleMap, useJsApiLoader } from '@react-google-maps/api';

const containerStyle = {
    width: '400px',
    height: '400px'
};
  
const center = {
    lat: -3.745,
    lng: -38.523
};

const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "YOUR_API_KEY"
  })

  const [map, setMap] = React.useState(null)

  const onLoad = React.useCallback(function callback(map) {
    // This is just an example of getting and using the map instance!!! don't just blindly copy!
    const bounds = new window.google.maps.LatLngBounds(center);
    map.fitBounds(bounds);

    setMap(map)
  }, [])

  const onUnmount = React.useCallback(function callback(map) {
    setMap(null)
  }, [])

class RegistroPontoLocalizacao extends React.Component {

    constructor(props) {
        super(props);
        this.state = { date: new Date() };
    }

    
    
    registrar() {
        $.ajax({
            url: 'http://localhost:8080/registroPonto/lista',
            type: 'GET',
            success: function(result) {
                console.log(result)
            }
        });
    }

    render() {
        return (
            <div> 
                <h3>Obtenha sua localização</h3>
                <span>Permita acesso no seu navegador.</span>
                <GoogleMap
                    mapContainerStyle={containerStyle}
                    center={center}
                    zoom={10}
                    onLoad={onLoad}
                    onUnmount={onUnmount}
                >
                    { /* Child components, such as markers, info windows, etc. */ }
                    <></>
                </GoogleMap>
            </div>
        );
    }
}

export default RegistroPontoLocalizacao;
