import logo from './café.jpg';
import './App.css';
import RegistroPonto from './RegistroPonto'

function App() {
  var data = new Date();
  return (
    <div className="App">
      <div>
        <h3>Bom dia</h3>
        <h2>Administrador</h2>
        <span>{data.toLocaleDateString()} - {getDay(data.getDay())}</span>
        <br/>
        <span>{data.toLocaleTimeString()}</span>
      </div>
      <div>
        <RegistroPonto />
      </div>
    </div>
  );
}

function getDay(numero) {
  switch(numero) {
    case 6:
      return 'Sábado'
    default: 
      return 'Meu cu'
  }
}


export default App;
