import React, { useState, useEffect, Component } from 'react';
import iconeClock from './icone-clock.png';

const [error, setError] = useState(null);
const [isLoaded, setIsLoaded] = useState(false);
const [items, setItems] = useState([]);

class RegistroPonto extends React.Component {

    constructor(props) {
        super(props);
        this.state = { date: new Date() };
    }
    
    registrar() { 
        fetch("https://api.example.com/items")
            .then(res => res.json())
            .then(
            (result) => {
                setIsLoaded(true);
                setItems(result);
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
                setIsLoaded(true);
                setError(error);
            }
            )
        }

   

    render() {
        return (
            <div style={{marginTop:"10px"}}>
                <label style={{ backgroundColor: "orange", color: "white", padding: "5px", borderRadius: "10px" }}>Aguardando Ponto</label>
                <br/>
                <br/>
                <span>Não há registros para o dia de hoje</span>
                <br/><br/>
                <button onClick={this.registrar} style={{borderRadius: "10px", backgroundColor: "grey"}}>
                    <img style={{width: '20px'}} src={iconeClock} /> <label style={{fontSize: '15px'}}>Registrar</label>
                </button>
            </div>
        );
    }
}

export default RegistroPonto;
