import React, { Component } from 'react';
import iconeClock from './icone-clock.png';
import $ from "jquery";
import { MapContainer } from 'https://cdn.esm.sh/react-leaflet/MapContainer'
import { TileLayer } from 'https://cdn.esm.sh/react-leaflet/TileLayer'
import { useMap } from 'https://cdn.esm.sh/react-leaflet/hooks'

class RegistroPontoLocalizacao extends React.Component {

    constructor(props) {
        super(props);
        this.state = { date: new Date() };
    }

    registrar() {
        $.ajax({
            url: 'http://localhost:8080/registroPonto/lista',
            type: 'GET',
            success: function (result) {
                console.log(result)
            }
        });
    }

    render() {
        return (
            <div>
                <h3>Obtenha sua localização</h3>
                <span>Permita acesso no seu navegador.</span>
                <br />
                <MapContainer center={[51.505, -0.09]} zoom={13} scrollWheelZoom={false}>
                    <TileLayer
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <Marker position={[51.505, -0.09]}>
                        <Popup>
                            A pretty CSS3 popup. <br /> Easily customizable.
                        </Popup>
                    </Marker>
                </MapContainer>
            </div>
        );
    }
}

export default RegistroPontoLocalizacao;
