import React, { Component } from 'react';
import { Map, Marker } from "pigeon-maps"

class Geolocalizacao extends React.Component {

    constructor(props) {
        super(props);
        this.state = {};
        this.bind(this);
    }

    componentDidMount() {
        let self = this;
        navigator.geolocation.getCurrentPosition(function(position) {
            self.setState({latitude: position.coords.latitude, longitude: position.coords.longitude});
        });
    }

    posicao = [this.latitude, this.longitude];

    render() {
        return (
            <div>
                <h3 style={{ marginTop: '30px', marginBottom: '1px' }}>Obtenha sua localização</h3>
                <span>Permita acesso no seu navegador.</span>
                <br />
                <Map height={280} width={460} defaultCenter={this.posicao} defaultZoom={17}>
                    <Marker width={50} color={''} anchor={this.posicao} />
                </Map>
            </div>
        );
    }
}

export default Geolocalizacao;
