import React, { Component } from 'react';
import Webcam from "react-webcam";
import $ from "jquery";

class Foto extends React.Component {

    salvarFoto = (base64) => {
        $.ajax({
            url: 'http://localhost:8080/registroPonto/salvarFoto',
            type: 'POST',
            data: {
                imageSrc: base64
            },
            success: function (result) {
                alert('Foto salva com sucesso!');
            }
        });
    }

    render() {
        const videoConstraints = {
            width: 1280,
            height: 450,
            facingMode: "user"
        };
        return (
            <div>
                <Webcam
                    audio={false}
                    height={720}
                    screenshotFormat="image/jpeg"
                    width={450}
                    videoConstraints={videoConstraints}
                >
                    {({ getScreenshot }) => (
                        <div style={{textAlign: 'center'}}>
                            <button
                                onClick={() => {
                                    const imageSrc = getScreenshot();
                                    this.salvarFoto(imageSrc);
                                }}
                                style={{ width: '150px', height: '40px', borderRadius: '10px', backgroundColor: 'lightgreen', fontSize: '12px'}}
                            >
                                Capturar Foto
                            </button>
                        </div>
                    )}
                </Webcam>
            </div>
        );
    }
}

export default Foto;
