import './App.css';
import RegistroPonto from './RegistroPonto';
import RegistrosSalvos from './RegistrosSalvos';
import Geolocalizacao from './Geolocalizacao';
import Cabecalho from './Cabecalho';

function App() {
  return (
    <div className="App">
      <Cabecalho />
      < Geolocalizacao />
      <RegistroPonto />
      <RegistrosSalvos />
    </div>
  );
}

function getDay(numero) {
  switch (numero) {
    case 1:
      return 'Segunda-feira';
    case 2:
      return 'Terça-feira';
    case 3:
      return 'Quarta-feira';
    case 4:
      return 'Quinta-feira';
    case 5:
      return 'Sexta-feira';
    case 6:
      return 'Sábado';
    case 0:
      return 'Domingo';
  }
}

function saudacao(hora) {
  if (hora >= '05:00:00' & hora < '12:00:00') {
    return 'Bom dia';
  } else if (hora >= '12:00:00' & hora < '18:00:00') {
    return 'Boa tarde';
  } else {
    return 'Boa noite';
  }
}

export default App;
