import React, { Component } from 'react';
import Cabecalho from './Cabecalho';
import Webcam from "react-webcam";
import $ from "jquery";

class Foto extends React.Component {

    salvarFoto = (base64) => {
        $.ajax({
            url: 'http://localhost:8080/registroPonto/salvarFoto',
            type: 'POST',
            data: {
                imageSrc: base64
            },
            success: function (result) {
                alert('Foto salva com sucesso!');
            }
        });
    }

    render() {
        const videoConstraints = {
            width: 1280,
            height: 720,
            facingMode: "user"
        };
        return (
            <div>
                <Cabecalho />
                <span>Uma selfie atual sua.</span>
                <br/>
                <span>Use um ambiente iluminado.</span>
                <div style={{marginTop: '-200px'}}>
                    <Webcam
                        audio={false}
                        height={720}
                        screenshotFormat="image/jpeg"
                        width={450}
                        videoConstraints={videoConstraints}
                    >
                        {({ getScreenshot }) => (
                            <div style={{ textAlign: 'center', marginTop: '-200px' }}>
                                <button
                                    onClick={() => {
                                        const imageSrc = getScreenshot();
                                        this.salvarFoto(imageSrc);
                                    }}
                                    style={{ position: 'relative', cursor: 'pointer', width: '150px', height: '40px', borderRadius: '10px', backgroundColor: 'lightgreen', fontSize: '15px' }}
                                >
                                    Tirar Foto
                                </button>
                            </div>
                        )}
                    </Webcam>
                </div>
            </div>
        );
    }
}

export default Foto;
