import React, { Component } from 'react';
import Webcam from "react-webcam";
import $ from "jquery";

class Foto extends React.Component {

    registrar = (base64) => {
        $.ajax({
            url: 'http://localhost:8080/registroPonto/salvarFoto',
            type: 'POST',
            data: {
                imageSrc : base64
            },
            success: function (result) {
                alert('Bombou');
            }
        });
    }

    render() {
        const videoConstraints = {
            width: 1280,
            height: 720,
            facingMode: "user"
        };
        return (
            <div>
                <Webcam
                    audio={false}
                    height={720}
                    screenshotFormat="image/jpeg"
                    width={450}
                    videoConstraints={videoConstraints}
                >
                    {({ getScreenshot }) => (
                        <button
                            onClick={() => {
                                const imageSrc = getScreenshot();
                                this.registrar(imageSrc);
                            }}
                        >
                            Capturar Foto
                        </button>
                    )}
                </Webcam>
            </div>
        );
    }
}

export default Foto;
