import React, { Component } from 'react';
import iconeClock from './icone-clock.png';
import $ from "jquery";


class RegistroPonto extends React.Component {

    constructor(props) {
        super(props);
        this.state = { date: new Date() };
    }

    buscar() {
        $.ajax({
            url: 'http://localhost:8080/registroPonto/lista',
            type: 'GET',
            success: function (result) {
                console.log(result)
            }
        });
    }

    render() {
        return (
            <div style={{ marginTop: "10px" }}>
                <label style={{ backgroundColor: "orange", color: "white", padding: "4px", borderRadius: "8px" }}>Aguardando Ponto</label>
                <br />
                <br />
                <button onClick={this.buscar} style={{ borderRadius: "10px", backgroundColor: "lightblue", padding: "4px" }}>
                <label style={{ fontSize: '15px'}}>Mostrar registros</label>
                </button>
                <br />
                <br />
                <button onClick={this.registrar} style={{ borderRadius: "10px", backgroundColor: "lightGreen", padding: "10px" }}>
                    <img style={{ width: '13px' }} src={iconeClock} /> <label style={{ fontSize: '15px'}}>Registrar</label>
                </button>
            </div>
        );
    }
}

export default RegistroPonto;
