import engrenagem from './engrenagem.png';
import './App.css';
import RegistroPonto from './RegistroPonto'

function App() {
  var data = new Date();
  return (
    <div className="App">
      <div>
        <nav>
          <h4 style={{marginBottom: "-15px"}}>Bem vindo</h4>
          <h2>Registro de Ponto</h2>
        </nav>
        <img src={engrenagem} />
        <h3>Bom dia</h3>
        <h2>Administrador</h2>
        <span>{data.toLocaleDateString()} - {getDay(data.getDay())}</span>
        <br/>
        <span>{data.toLocaleTimeString()}</span>
      </div>
      <div>
        <RegistroPonto/>
      </div>
    </div>
  );
}

function getDay(numero) {
  switch (numero) {
    case 1:
      return 'Segunda-feira';
    case 2:
      return 'Terça-feira';
    case 3: 
      return 'Quarta-feira';
    case 4: 
      return 'Quinta-feira';
    case 5: 
      return 'Sexta-feira';
    case 6: 
      return 'Sábado';
    case 0: 
      return 'Domingo';
  }
}


export default App;
